import React, { useRef, useState } from 'react';
import '../../styles/tour-details.css';
import { Container, Row, Col, Form, ListGroup } from 'reactstrap';
import { useParams } from 'react-router-dom';
import calculateAvgRating from '../../utils/avgRating';
import avatar from '../../assets/images/avatar.jpg';
import Booking from '../Booking/Booking';
import Newsletter from '../../shared/Newsletter';
import useFetch from '../../hooks/useFetch';
import { BASE_URL } from '../../utils/config';

const TourDetails = () => {
  const { id } = useParams();
  const reviewMsgRef = useRef('');
  const [tourRating, setTourRating] = useState(null);

  // Fetch tour data
  const { data: tour } = useFetch(`${BASE_URL}/tours/${id}`);

  // Destructure properties from the tour object
  const { photo, title, desc, price, address, reviews, city, distance, maxGroupSize } = tour;

  // Calculate average rating
  const { totalRating, avgRating } = calculateAvgRating(reviews);

  // Date formatting options
  const options = { day: 'numeric', month: 'long', year: 'numeric' };

  // Submit review handler
  const submitHandler = (e) => {
    e.preventDefault();
    const reviewText = reviewMsgRef.current.value;
    // TODO: Submit review to the server
    // alert(`${reviewText}, ${tourRating}`);
  };

  return (
    <>
      <section>
        <Container>
          <Row>
            <Col lg='8'>
              <div className='tour__content'>
                <img src={photo} alt='' />
                <div className='tour__info'>
                  <h2>{title}</h2>
                  <div className='d-flex align-items-center gap-5'>
                    <span className='tour__rating d-flex align-items-center gap-1'>
                      <i className='ri-star-fill' style={{ color: 'var(--secondary-color)' }}></i>
                      {avgRating === 0 ? null : avgRating}
                      {totalRating === 0 ? (
                        'Not rated'
                      ) : (
                        <span>({reviews.length})</span>
                      )}
                    </span>

                    <span>
                      <i className='ri-map-pin-fill'></i>
                      {address}
                    </span>
                  </div>
                  <div className='tour__extra-details'>
                    <span>
                      <i className='ri-map-pin-2-line'></i>
                      {city}
                    </span>
                    <span>
                      <i className='ri-money-dollar-circle-line'></i>
                      ${price} per Person
                    </span>
                    <span>
                      <i className='ri-pin-distance-line'></i>
                      {distance} K/M
                    </span>
                    <span>
                      <i className='ri-group-line'></i>
                      {maxGroupSize} people
                    </span>
                  </div>
                  <h5>Description</h5>
                  <p>{desc}</p>
                </div>

                {/* Reviews section start */}

                <div className='tour__reviews mt-4'>
                  <h4>Reviews ({reviews?.length} reviews)</h4>

                  <Form onSubmit={submitHandler}>
                    <div className='d-flex align-items-center gap-3 mb-4 rating__group'>
                      {[1, 2, 3, 4, 5].map((rating) => (
                        <span key={rating} onClick={() => setTourRating(rating)}>
                          {rating} <i className='ri-star-s-fill'></i>
                        </span>
                      ))}
                    </div>
                    <div className='review__input'>
                      <input type='text' ref={reviewMsgRef} placeholder='Rate your Experience' required />
                      <button className='btn primary__btn text-white' type='submit'>
                        Submit
                      </button>
                    </div>
                  </Form>

                  <ListGroup className='user__reviews'>
                    {reviews?.map((review) => (
                      <div className='review__item' key={review.id}>
                        <img src={avatar} alt='' />
                        <div className='w-100'>
                          <div className='d-flex align-items-center justify-content-between'>
                            <div>
                              <h5>{review.user}</h5>
                              <p>{new Date(review.date).toLocaleDateString('en-US', options)}</p>
                            </div>
                            <span className='d-flex align-items-center'>
                              {review.rating}<i className='ri-star-s-fill'></i>
                            </span>
                          </div>
                          <h6>{review.comment}</h6>
                        </div>
                      </div>
                    ))}
```javascript
                  </ListGroup>
                </div>
              </div>
            </Col>

            <Col lg='4'>
              <Booking tour={tour} avgRating={avgRating} />
            </Col>
          </Row>
        </Container>
      </section>
      <Newsletter />
    </>
  );
};

export default TourDetails;
